import React, { useState, useEffect } from 'react';
import '../../css/dedicated/conversor.css'

export default function CurrencyConverter(props) {



  // Define la lista de divisas que deseas mostrar
  var currenciesToShow = ['ARS', 'USD', 'EUR', 'UYU'];
  
  const [selectedCurrency, setSelectedCurrency] = useState('ARS');
  const [amount, setAmount] = useState('');
  const [convertedValues, setConvertedValues] = useState({
    ARS: 0,
    USD: 0,
    EUR: 0,
    UYU: 0,
  });



  const handleCurrencyChange = (e) => {
    setSelectedCurrency(e.target.value);
  };

  const handleAmountChange = (e) => {
    setAmount(e.target.value);
  };

  const convertCurrency = (amount, currency) => {
    

    // Obtén las tasas de cambio de ExchangeRatesAPI
    const apiKey = '94a245608c19ff1677cd843a';
    const apiUrl = `https://api.exchangerate-api.com/v4/latest/${selectedCurrency}`;

    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => {
        const rates = data.rates;

        // Filtra las tasas de cambio para incluir solo las divisas deseadas
        const convertedValues = {};
        currenciesToShow.forEach((currency) => {
          convertedValues[currency] = (amount * rates[currency]).toFixed(2);
        });

        setConvertedValues(convertedValues);
      })
      .catch((error) => {
        console.error('Error al obtener tasas de cambio:', error);
      });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      convertCurrency(amount, selectedCurrency);
    }
  };

  useEffect(() => {
    // Realiza la conversión inicial al cargar el componente
    convertCurrency(1, selectedCurrency);
  }, [selectedCurrency]);



  
    return (
      <div className={props.show ? "popUp" : "popUp-hide"}>
        <div className='form-container'>
          <h4>Conversor de Moneda</h4>
          <div>
            <label>Moneda Base:</label>
            <select value={selectedCurrency} onChange={handleCurrencyChange}>
              <option value="ARS">ARS</option>
              <option value="USD">USD</option>
              <option value="EUR">EUR</option>
              <option value="UYU">UYU</option>
            </select>
          </div>
          <div>
            <label>Cantidad:</label>
            <input
              type="number"
              value={amount}
              onChange={handleAmountChange}
            />
          </div>
          <div>
            <h5>Tasas de Cambio:</h5>
            <ul>
              {currenciesToShow.map((currency) => (
                <p key={currency}>
                  {currency}: ${convertedValues[currency]}
                </p>
              ))}

            </ul>
          </div>
        </div>
      </div>
    )
  }
