import React, { useState, useEffect } from 'react';
import '../../css/dedicated/conversor.css'

export default function CurrencyConverter(props) {

  const [selectedCurrency, setSelectedCurrency] = useState('ARS');
  const [amount, setAmount] = useState('');
  const [convertedValues, setConvertedValues] = useState({
    ARS: 0,
    USD: 0,
    EUR: 0,
    UYU: 0,
  });

  const handleCurrencyChange = (e) => {
    setSelectedCurrency(e.target.value);
    convertCurrency(amount, e.target.value);
  };

  const handleAmountChange = (e) => {
    setAmount(e.target.value);
  };

  const convertCurrency = (amount, currency) => {
    // Obtén las tasas de cambio de ExchangeRatesAPI
    const apiKey = 'TU_CLAVE_DE_API';
    // Realiza la solicitud a la API al cargar el componente y cuando cambia la moneda base o la cantidad
    const apiUrl = `https://api.exchangerate-api.com/v4/latest/${selectedCurrency}`;

    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => {
        const rates = data.rates;

        const convertedValues = {
          ARS: (amount * rates.ARS).toFixed(2),
          USD: (amount * rates.USD).toFixed(2),
          EUR: (amount * rates.EUR).toFixed(2),
          UYU: (amount * rates.UYU).toFixed(2),
        };

        setConvertedValues(convertedValues);
      })
      .catch((error) => {
        console.error('Error al obtener tasas de cambio:', error);
      });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      convertCurrency(amount, selectedCurrency);
    }
  };

  useEffect(() => {
    // Realiza la conversión inicial al cargar el componente
    convertCurrency(1, selectedCurrency);
  }, [selectedCurrency]);


  return (
    <div className={props.show ? "popUp" : "popUp-hide"}>
      <div className='conversor-container'>
        <div className="form-container">
          <select id="currency">
            <option value="ARS">Peso Argentino</option>
            <option value="USD">Dólar Oficial</option>
            <option value="EUR">Euro Oficial</option>
            <option value="UYU">Peso Uruguayo</option>
          </select>
          <input type="number" id="amount" className="amount" placeholder="Monto" />
          <button id="convert" className="general-btn" draggable="false">Convertir</button>
        </div>

        <div id="result">
          <ul className="number-format">
            <div>
              <label><img src={require('../../resources/flags/ars.png')} draggable="false" className="flag" />Pesos Argentinos:</label>
              <li id="ARS">$0.00</li>
            </div>
            <div>
              <label><img src={require('../../resources/flags/usd.png')} draggable="false" className="flag" />Dólar Oficial:</label>
              <li id="USD">$0.00</li>
            </div>
            <div>
              <label><img src={require('../../resources/flags/eur.png')} draggable="false" className="flag" />Euro Oficial:</label>
              <li id="EUR">$0.00</li>
            </div>
            <div>
              <label><img src={require('../../resources/flags/uyu.png')} draggable="false" className="flag" />Pesos Uruguayos:</label>
              <li id="UYU">$0.00</li>
            </div>
          </ul>
        </div>
      </div>
    </div>
  )
};
