import { password, passwordError, isSubmitDisabled} from '../js/login-functionality';
 
/*return (
    <div className="login">
      <form action>
        <h4>Ingresa a tu cuenta</h4>
        <label>Correo electrónico:</label>
        <input type="text" className="mail" id="nombre" placeholder="correo electrónico" required />
        <label>Contraseña:</label>
        <input type="password" className="password" id="password" placeholder="contraseña" required />
        <Link to="/home" className='button--general login-btn'>Ingresar</Link>
        <Link to="/registro">Crear una cuenta</Link>
      </form>
    </div>
  )
}
*/
export default function LoginContent() {
  
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);

return (
    <div className="login">
      <form onSubmit={handleSubmit}>
        <h4>Ingresa a tu cuenta</h4>
        <label>Correo electrónico:</label>
        <input type="text" className="mail" id="nombre" placeholder="correo electrónico" required />
        <label>Contraseña:</label>
        <input type="password" className="password" id="password" placeholder="contraseña" required /><span className="errorMessagePassword">
        {passwordError}
      </span>
        <Link to="/home" className='button--general login-btn' disabled={isSubmitDiasabled}>Ingresar</Link> 
        <Link to="/registro">Crear una cuenta</Link>
      </form>
    </div>
  )
}

