import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

import '../css/dedicated/login-content.css';
 
/*return (
    <div className="login">
      <form action>
        <h4>Ingresa a tu cuenta</h4>
        <label>Correo electrónico:</label>
        <input type="text" className="mail" id="nombre" placeholder="correo electrónico" required />
        <label>Contraseña:</label>
        <input type="password" className="password" id="password" placeholder="contraseña" required />
        <Link to="/home" className='button--general login-btn'>Ingresar</Link>
        <Link to="/registro">Crear una cuenta</Link>
      </form>
    </div>
  )
}
*/
export default function LoginContent() {

  const [newPassword, setNewPassword] = useState('')
  const [passwordError, setPasswordError] = useState('');
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);

  function verifyLogin(){
    var passwordInput = document.querySelector('#password')
    setNewPassword(passwordInput.value)

    if (newPassword.length < 8) {
      setPasswordError('La contraseña debe tener al menos 8 caracteres');
      setIsSubmitDisabled(true);
    } else if (!/[A-Z]/.test(newPassword) || !/[0-9]/.test(newPassword) || !/[!@#$%^&*]/.test(newPassword)) {
      setPasswordError('La contraseña debe contener al menos una letra mayúscula, un número y un carácter especial');
      setIsSubmitDisabled(true);
    } else {
      window.location.href('/home')
    }
  };
  

return (
    <div className="login">
      <form>
        <h4>Ingresa a tu cuenta</h4>
        <label>Correo electrónico:</label>
        <input type="text" className="mail" id="correo" placeholder="correo electrónico" required />
        <label>Contraseña:</label>
        <input type="password" className="password" id="password" placeholder="contraseña" required />
        <Link onClick={verifyLogin} className='button--general login-btn'>Ingresar</Link><span style={"color = red"}>{passwordError}</span> 
        <Link to="/registro">Crear una cuenta</Link>
      </form>
    </div>
  )
}

