export default function loginFunctionality(){

  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);
  
  const handlePasswordChange = (e) => {
    var newPassword = e.target.value;
    setPassword(newPassword);

    if (newPassword.length < 8) {
      setPasswordError('La contraseña debe tener al menos 8 caracteres');
      setIsSubmitDisabled(true);
    } else if (!/[A-Z]/.test(newPassword) || !/[0-9]/.test(newPassword) || !/[!@#$%^&*]/.test(newPassword)) {
      setPasswordError('La contraseña debe contener al menos una letra mayúscula, un número y un carácter especial');
      setIsSubmitDisabled(true);
    } else {
      setPasswordError('');
      setIsSubmitDisabled(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Puedes realizar alguna acción aquí si es necesario, pero no es necesario cambiar isSubmitDisabled
  }
}
